window.addEventListener("scroll",(function(){var scrollToTopButton=document.getElementById("scrollToTopButton");if(window.pageYOffset>100){scrollToTopButton.style.display="block"}else{scrollToTopButton.style.display="none"}}));function scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})}var scrollToTopButton=document.getElementById("scrollToTopButton");scrollToTopButton.addEventListener("click",scrollToTop);var visitorCount=localStorage.getItem("visitorCount");if(!visitorCount){visitorCount=0}visitorCount++;localStorage.setItem("visitorCount",visitorCount);document.getElementById("visitorCount").textContent=visitorCount;